import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Route, Switch, Link } from 'react-router-dom';
import SignupFormPage from './components/SignupFormPage';
import LoginFormPage from "./components/LoginFormPage";
import Navigation from './components/Navigation';
import Splash from './components/Splash';
import KitchenComponent from './components/KitchenComponent';
import * as sessionActions from './store/session';
import * as kitchenActions from './store/kitchen'
import { Modal } from './context/Modal';
import SVme from './media/images/pnMe.png'


function App() {
  const dispatch = useDispatch();
  const [isLoaded, setIsLoaded] = useState(false);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    dispatch(sessionActions.restoreUser()).then(() => setIsLoaded(true));
  }, [dispatch]);

  return (
    <>
      <Navigation isLoaded={isLoaded} />
      <img src={SVme} onClick={() => setShowModal(true)} id='aboutMeImg' />
      {showModal && (
        <Modal onClose={() => setShowModal(false)}>
          <h2>Project By:</h2>
          <h1>Kathleen Young</h1>
          <div id='aboutMeLinks'>
            <Route to='https://github.com/cheshirekate8/' exact>
              <i class="fab fa-github"></i>
            </Route>
            <Route to='www.linkedin.com/in/dev-kathleen-young'>
              <i class="fab fa-linkedin-in"></i>
            </Route>
            <Route to='https://angel.co/u/kathleen-young-3'>
              <i class="fab fa-angellist"></i>
            </Route>
            {/* <i class="fas fa-file"></i> */}
          </div>

        </Modal>
      )}
      {isLoaded && (
        <Switch>
          <Route path="/" exact >
            <Splash />
          </Route>
          <Route path="/kitchen">
            <KitchenComponent />
          </Route>
          <Route path="/login" >
            <LoginFormPage />
          </Route>
          <Route path='/signup'>
            <SignupFormPage />
          </Route>
        </Switch>
      )}
    </>
  );
}

export default App;
